const telefoneOriginal = $node["separa_em_linhas"].json["telefone"].toString();

// Removendo caracteres não numéricos
const telefoneLimpo = telefoneOriginal.replace(/\D/g, '');

// Define o DDI padrão
const ddiPadrao = '55';

// Extrai o DDD e o número, considerando que o telefone limpo pode já vir com o DDI
let ddd, numero;
if (telefoneLimpo.startsWith(ddiPadrao)) {
    // Se o número já começa com o DDI padrão, extrai sem o DDI
    ddd = telefoneLimpo.slice(2, 4);
    numero = telefoneLimpo.slice(4);
} else {
    // Se o número não contém o DDI, extrai o DDD e o número como estão
    ddd = telefoneLimpo.slice(0, 2);
    numero = telefoneLimpo.slice(2);
}

// Monta o número de telefone final, adicionando o DDI apenas se não estava presente
const telefoneFinal = telefoneLimpo.startsWith(ddiPadrao) ? ddiPadrao + ddd + numero : ddiPadrao + telefoneLimpo;

return { telefonefinal: telefoneFinal };

