const telefoneClienteOriginal = $node["separa_em_lotes6"].json["telefone"].toString() || "";

let telefoneFinal = "";

if (telefoneClienteOriginal) {
    const telefoneLimpo = telefoneClienteOriginal.replace(/\D/g, "");

    let DDI = '';
    let DDD = '';
    let numero = '';

    if (telefoneLimpo.length > 11) {
        DDI = telefoneLimpo.slice(0, 2);
        DDD = telefoneLimpo.slice(2, 4);
        numero = telefoneLimpo.slice(4);
    } else if (telefoneLimpo.length === 11) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else if (telefoneLimpo.length === 10) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else {
        telefoneFinal = telefoneLimpo;
    }

    if (DDD && numero) {
        if (parseInt(DDD) > 28) {
            if (numero.length === 9 && numero.startsWith("9")) {
                numero = numero.slice(1);
            }
        } else {
            if (numero.length === 8) {
                numero = `9${numero}`;
            }
        }
    }

    if (DDI && DDD && numero) {
        telefoneFinal = `${DDI}${DDD}${numero}`;
    }
}

const result = {
    whatsapp: telefoneFinal
};

return [{ json: result }];

