// Função para encontrar a instância com base no proprietário
function encontrarInstanciaPorProprietario(proprietario, instancias) {
    const instanciaKeys = Object.keys(instancias);
    for (const key of instanciaKeys) {
        const instancia = instancias[key].instance;
        console.log("instancia:", instancia);
        if (instancia && instancia.owner === proprietario) {
            return instancia;
        }
    }
    return null;
}

// Encontrar instância do grupo
const instanciaGrupos = encontrarInstanciaPorProprietario(wppGrupo, instancias);

// Encontrar instância de 1x1 para captação
const instancia1x1Captacao = encontrarInstanciaPorProprietario(wpp1x1Captacao, instancias);

// Encontrar instância de 1x1 para vendas
const instancia1x1Vendas = encontrarInstanciaPorProprietario(wpp1x1Vendas, instancias);

// Retornar os resultados
return {
    "instancia_grupos": instanciaGrupos,
    "instancia_1x1_captacao": instancia1x1Captacao,
    "instancia_1x1_vendas": instancia1x1Vendas
};

