const respostas = $node["separa_linhas"].json["custom_fields"] || [];

// Pegando o campo específico 'Tipo de Mensagem'
let tipo_msg_field = respostas.find(answer => answer.name === "Tipo de Mensagem");
let tipo_msg = "Desconhecido";

// Pegando o campo específico 'Destino da Mensagem'
let destino_msg_field = respostas.find(answer => answer.name === "Destino da Mensagem");
let destino_msg = "Desconhecido";

// Pegando o campo específico 'Versão funil'
let versao_funil = respostas.find(answer => answer.name === "Versão funil")?.value || "";

// Verifica se o campo 'Tipo de Mensagem' existe e tem opções configuradas
if (tipo_msg_field && tipo_msg_field.type_config && Array.isArray(tipo_msg_field.type_config.options)) {
    let tipo_msg_value = tipo_msg_field.value;

    // Encontra a opção correspondente ao valor de 'orderindex'
    const tipoOption = tipo_msg_field.type_config.options.find(opt => opt.orderindex === tipo_msg_value);

    // Se encontrar a opção, pega o 'name'
    if (tipoOption) {
        tipo_msg = tipoOption.name;
    }
}

// Verifica se o campo 'Destino da Mensagem' existe e tem opções configuradas
if (destino_msg_field && destino_msg_field.type_config && Array.isArray(destino_msg_field.type_config.options)) {
    let destino_msg_value = destino_msg_field.value;

    // Encontra a opção correspondente ao valor de 'orderindex'
    const destinoOption = destino_msg_field.type_config.options.find(opt => opt.orderindex === destino_msg_value);

    // Se encontrar a opção, pega o 'name'
    if (destinoOption) {
        destino_msg = destinoOption.name;
    }
}

// Função para extrair o link diretamente do campo 'value_richtext'
function extractLinkFromRichText(valueRichText) {
    try {
        const parsedRichText = JSON.parse(valueRichText);
        // Procurando diretamente o campo de link
        const link = parsedRichText.ops.find(op => op.insert?.link)?.insert.link;
        return link ? link : "";
    } catch (error) {
        console.log("Erro ao processar value_richtext:", error);
        return "";
    }
}

// Pegando o valor do arquivo (link de mídia) do campo 'Base64 da Mídia'
let arquivoField = respostas.find(answer => answer.name === "Base64 da Mídia");
let arquivo = arquivoField ? arquivoField.value : "";  // Usando o campo value diretamente para o link

// Pegando outros campos
let evento = respostas.find(answer => answer.name === "Evento")?.value || "";
let funil = respostas.find(answer => answer.name === "Funil")?.value || "";
let copy = $node["separa_linhas"].json["text_content"] || "";

// Retorna os valores processados com os nomes ajustados
return {
  arquivo,
  tipo_msg,
  destino_msg,
  versao_funil,
  copy,
  evento,
  funil
};

// Input dinâmico (você pode substituir isso pelo input real no n8n)
const input = $input.all();

// Verificar se há tarefas no input
const tarefasSeparadas = [];

input.forEach(item => {
  if (item.json.tasks && Array.isArray(item.json.tasks)) {
    // Iterar sobre todas as tarefas e separá-las
    item.json.tasks.forEach(tarefa => {
      tarefasSeparadas.push({
        json: tarefa // Mantém todos os dados da tarefa
      });
    });
  }
});

// Retornar as tarefas separadas
return tarefasSeparadas;

