// Obter a data atual
const dataAtual = new Date();

// Função para formatar a data no formato "YYYY-MM-DD"
function formatarData(data) {
  const ano = data.getFullYear();
  const mes = String(data.getMonth() + 1).padStart(2, '0'); // Meses começam em 0
  const dia = String(data.getDate()).padStart(2, '0');
  return `${ano}-${mes}-${dia}`;
}

// Objeto para armazenar as datas semanais
const datasSemanais = {};

// Loop para calcular as datas de cada semana
for (let i = 1; i <= 6; i++) {
  const dataSemana = new Date(dataAtual); // Criar uma nova data baseada na data atual
  dataSemana.setDate(dataAtual.getDate() + (i * 7)); // Adicionar semanas
  datasSemanais[`data_semana${i}`] = formatarData(dataSemana); // Adicionar ao objeto
}

// Retornar as datas formatadas no formato esperado pelo n8n
return [{ json: datasSemanais }];
