const emailFormatado = tratarEmail(email);
const cpfCnpjFormatado = formatarCpfCnpj(cpfCnpj);
const descricaoFormatada = validarEDescricao(descricao);

// Criar objeto com os valores formatados
const formattedData = {
  nome: capitalizeWords(nome),
  telefone: telefoneFormatado,
  email: emailFormatado,
  cpf_cnpj: cpfCnpjFormatado,
  descricao: descricao,
  tipo_mentoria: descricaoFormatada
};

// Retorna o objeto formatado como JSON
return formattedData;

